const scriptsRoute = require('./routes/scriptRoutes');
const authRoute = require('./routes/authRoutes');
const userRoute = require('./routes/userRoutes');
const profileRoute = require('./routes/profileRoutes');

let app = express();
let port = process.env.PORT || 8080;

// Mongoose instance connection url connection
const databaseUrl = process.env.LD_DB_URL;
mongoose.Promise = global.Promise;

/*
 * Connect to database
 */

var connectWithRetry = function () {
    return mongoose.connect(databaseUrl, function (err) {
        if (err) {
            console.warn(
                'Failed to connect to mongo on startup - retrying in 5 sec'
            );
            setTimeout(connectWithRetry, 5000);
        }
    });
};
connectWithRetry();

passport.use(jwtAuthStrategy);

app.use(express.json({ limit: '50Mb' }));
app.use(cors());
app.use(passport.initialize());

app.set('etag', false);
app.use((req, res, next) => {
    res.set('Cache-Control', 'no-store');
    next();
});

/*
 * Routes
 */
app.use('/auth', authRoute);
app.use('/users', userRoute);
app.use('/profile', passport.authenticate('jwt', { session: false }), profileRoute);
app.use(
    '/scripts',
    passport.authenticate('jwt', { session: false }),
    scriptsRoute
);

app.listen(port);
